const initialData = {
    list : []
};

const todoReduce = (state = initialData , action) =>{
   switch (action.type){
       
    case 'Add-Todo':

        const {id, data} = action.payload;    
        return {
            ...state,
             list : [...state.list,{ id : id, data : data,} ]
        };
    
    case 'remove-Todo' : 
            const indx = action.payload;
            list.filter((id) =>{
                return id !== indx
            })
           

    default : return state
   }
}
export default todoReduce;
