const initialData = {
    list : []
};

const todoReduce = (state = initialData , action) =>{
   switch (action.type){
       
    case 'Add-Todo':

        const {id, data} = action.payload;    
        
        (!data) ? null : {...state,list : [...state.list,{ id : id, data : data,} ]};
    
    case 'remove-Todo' : 
    const newList = state.list.filter((elem) =>  elem.id !== action.id );
    return{
        ...state,
        list : newList
        };

    case 'remove-List':
        return {
            ...state,
            list : []} 
           

    default : return state
   }
}
export default todoReduce;
