const initialData = {
    list : []
};

const todoReduce = (state = initialData , action) =>{
   switch (action.type){
       
    case 'Add-Todo':

        const {id, data, toggle} = action.payload;    
        
        if(!data){
           return  {list : [...state.list]};
        }else {return{...state,list : [...state.list,{ id : id, data : data,} ]}}; 
    
    case 'remove-Todo' : 
    const newList = state.list.filter((elem) =>  elem.id !== action.id );
    return{
        ...state,
        list : newList,
        };

    case 'remove-List':
        return {
            ...state,
            list : []} 
           
    case 'Edit-Todo':

        let index = action.index;
        const editItem = state.list.find((elem)=>{ return elem.id === index});
         
        //  console.log(editItem.data)
         
         return{
             ...state,
             newId : editItem.id,
             newInput : editItem.data,}
        //  return inputData : setInput(newInput)  ;
        //  console.log(index, input)
                 
    default : return state
   }
}
export default todoReduce;
