const initialData = {
    list : []
};

const todoReducer = (state = initialData , action) =>{
   switch (action.type){
       
    case 'Add-Todo':

        const [id, data] = action.payload;    
        return {
            ...state,
             list : [...state.list,{ id : id, data : data,} ]
           
       };

     default :state
   }
}
export default todoReducer;
