export const addTodo = (input) =>{
    return{
        type : 'Add-Todo',
        payload : {
            id : new Date().getTime().toString(),
            data : input,
            },
    }

}

export const removeTodo = (id) =>{
    return{
        type : 'remove-Todo',
        id,
    }

}

export const  removeAllTodo = () =>{
    return{
        type : 'remove-List',
    }

}

export const editTodo = (indx, input) =>{
   return{
       type: 'Edit-Todo',
        payload : {
           index : indx,
           data : input, input,
               }
   }
}
